<script>
	import { createEventDispatcher } from 'svelte';

	export let _screen;
	export let isWide = false;
	export let footerText;

	const dispatch = createEventDispatcher();
	const close = () => {
		dispatch('close');
	}
	$: footerText = footerText || 'Click on background to dismiss';
</script>

<div
	aria-label="Banner"
	class='Banner clickable {$_screen?.classes}'
	role='alert'
	on:click={close}
>
	<div class='inner' class:narrow={!isWide} on:click|stopPropagation>
		<div class='content'>
			<div class='body'>
				<slot name='body' />
			</div>

			<div class='footer'>
				<slot name='footer'>
					<div class='defaultfooter'>
						{footerText}
					</div>
				</slot>
			</div>
		</div>
	</div>
</div>

