<script>
	import geoViewport from '@mapbox/geo-viewport';
	import bbox from '@turf/bbox';
	import mapboxgl from 'mapbox-gl';
	import {beforeUpdate, onMount} from 'svelte';

	import {toGeoPoints} from 'app/utils/geoUtils';

	import {
		FIT_PADDING,
		MAPBOXGL_MAX_ZOOM,
		MAPBOXGL_MIN_ZOOM,
		MAPBOXGL_TILE_SIZE
	} from './consts';

	export let accessToken;
	export let styleURL;
	export let withScaleControl;
	export let withZoomControl;

	/* state */

	let map;
	let mapCreated;
	let mapcontainer;
	let height = 0;
	let width = 0;

	export let isInteractive = true; // has to be true initially

	// temporary point list to compute bounding box
	$: boundingBox = bbox(toGeoPoints([
		{
			Longitude: -7.57216793459,
			Latitude: 49.959999905
		},
		{
			Longitude: 1.68153079591,
			Latitude: 58.6350001085
		}
	]));
	$: viewport = geoViewport.viewport(
		boundingBox,
		[width, height],
		MAPBOXGL_MIN_ZOOM,
		MAPBOXGL_MAX_ZOOM,
		MAPBOXGL_TILE_SIZE
	);

	/* lifecycle */

	beforeUpdate(() => {
		if (viewport && height > 0 && !mapCreated) {
			mapCreated = true;
			createMap();
		}
	})

	onMount(() => {
		setGeometry();
	});

	/* methods */

	const setGeometry = () => {
		if (!mapcontainer) {
			return
		};

		const elementGeometry = getComputedStyle(mapcontainer);
		width = parseFloat(elementGeometry.width);
		height = parseFloat(elementGeometry.height);
	};

	const createMap = () => {
		const {center, zoom} = viewport;

		mapboxgl.accessToken = accessToken;

		map = new mapboxgl.Map({
			container: mapcontainer,
			center,
			zoom,
			style: styleURL,
			renderWorldCopies: false,

			// interactions
			scrollZoom: isInteractive,
			dragPan: isInteractive,
			dragRotate: false,
			pitchWithRotate: false, // don't render dots in perspective

			attributionControl: false, // we add this later to have it compact
		})
		.on('load', () => {
			fitBoundsToItems();
			setGeometry(); // ipad FIXME: initial svg is 100x100
		});

		addControls();
	};

	/* controls */

	const addControls = () => {
		addAttributionControl();

		if (withScaleControl) {
			addScaleControl();
		}

		if (withZoomControl) {
			addZoomControl();
		}
	};

	const addAttributionControl = () => {
		map.addControl(
			new mapboxgl.AttributionControl({
				compact: true
			})
		)
	};

	const addScaleControl = () => {
		map.addControl(
			new mapboxgl.ScaleControl({
				maxWidth: 80,
				unit: 'metric'
			}),
			'bottom-right'
		);
	};

	const addZoomControl = () => {
		map.addControl(
			new mapboxgl.NavigationControl({
				showCompass: false
			}),
			'bottom-left'
		);
	};

	/* zoom */

	const fitBoundsToItems = () => {
		map.fitBounds(boundingBox, {
			linear: true,
			padding: {
				bottom: FIT_PADDING,
				left: FIT_PADDING,
				right: FIT_PADDING,
				top: FIT_PADDING,
			}
		});
	};
</script>

<svelte:head>
	<link rel='stylesheet' type='text/css' href='/css/mapbox-gl.css'>
</svelte:head>

<svelte:window
	on:resize={setGeometry}
/>

<div class='layers'>
	<div
		bind:this={mapcontainer}
		class='mapcontainer'
	></div>
</div>

<style>
	.layers {
		width: 100%;
		height: 100%;
		position: relative;
	}

	.mapcontainer {
		width: 100%;
		height: 100%;
	}
</style>
