<script>
	import * as _ from 'lamb';

	import {_dataset} from 'app/stores/dataset';
	import {
		_focusedMarkerId,
		_focusedOrg,
		_selectedMarkerId
	} from 'app/stores/interaction';
	import {_isSmallScreen} from 'app/stores/layout';

	export let getLonLat = _.identity;
	export let items = [];
	export let projectFn = _.identity;

	$: projectItem = item => ({...item, ...projectFn(getLonLat(item))});
	$: markers = projectFn && getLonLat && _.map(items, projectItem) || [];
	$: focusedMarker =
		$_focusedOrg && projectFn && getLonLat &&
		projectFn(getLonLat($_focusedOrg));
</script>

<g class='SvgMarkers'>
	{#each markers as {x, y, id}}
		{#if $_isSmallScreen}
			<circle
				class='marker'
				cx={x}
				cy={y}
				r=3
				on:click={() => _selectedMarkerId.set(id)}
			/>
		{:else}
			<circle
				class='marker'
				cx={x}
				cy={y}
				r=3
				on:click={() => {
					_selectedMarkerId.set(id);
					_focusedMarkerId.set(null);
				}}
				on:mouseenter={() => _focusedMarkerId.set(id)}
				on:mouseleave={() => _focusedMarkerId.set(null)}
			/>
		{/if}
	{/each}
	{#if focusedMarker}
		<circle
			class='marker focused'
			cx={focusedMarker.x}
			cy={focusedMarker.y}
			r=6
		/>
	{/if}
</g>

<style>
	.marker {
		fill-opacity: 0.75;
		cursor: pointer;
	}

	.focused {
		fill: red;
	}
</style>
