<script>
	import * as _ from 'lamb';

	import {_dataset} from 'app/stores/dataset';
	import {
		_focusedMarkerId,
		_focusedMarker,
		_focusedOrg,
		_selectedMarkerId,
		_selectedMarker
	} from 'app/stores/interaction';
	import {_isSmallScreen} from 'app/stores/layout';

	export let getLonLat = _.identity;
	export let items = [];
	export let projectFn = _.identity;

	const setFocusedMarker = id => {
		!$_selectedMarker && _focusedMarkerId.set(id);
	};

	const setSelectedMarker = id => {
		_selectedMarkerId.set(id);
		_focusedMarkerId.set(null);
	};

	$: projectItem = item => ({...item, ...projectFn(getLonLat(item))});
	$: markers = projectFn && getLonLat && _.map(items, projectItem) || [];
	$: focusedMarker =
		($_focusedMarker || $_focusedOrg) && projectFn && getLonLat &&
		projectFn(getLonLat($_focusedMarker || $_focusedOrg));
	$: selectedMarker =
		$_selectedMarker && projectFn && getLonLat &&
		projectFn(getLonLat($_selectedMarker));
</script>

<g class='SvgMarkers'>
	{#each markers as {x, y, id}}
		{#if $_isSmallScreen}
			<circle
				class='marker'
				cx={x}
				cy={y}
				r=3
				on:click={() => setSelectedMarker(id)}
			/>
		{:else}
			<circle
				class='marker'
				cx={x}
				cy={y}
				r=3
				on:click={() => setSelectedMarker(id)}
				on:mouseenter={() => setFocusedMarker(id)}
				on:mouseleave={() => setFocusedMarker(null)}
			/>
		{/if}
	{/each}
	{#if focusedMarker}
		<circle
			class='marker focused'
			cx={focusedMarker.x}
			cy={focusedMarker.y}
			r=6
		/>
	{/if}
	{#if selectedMarker}
		<circle
			class='marker focused'
			cx={selectedMarker.x}
			cy={selectedMarker.y}
			r=6
		/>
	{/if}
</g>

<style>
	.marker {
		fill-opacity: 0.75;
		cursor: pointer;
	}

	.focused {
		fill: red;
		pointer-events: none;
	}
</style>
