<script>
	import {_screen} from '@svizzle/ui/src/sensors/screen/ScreenSensor.svelte';

	import DetailCard from 'app/components/details/DetailCard.svelte';
	
	import {_orgBannerFooter, clearHero} from 'app/stores/interaction';

	import Banner from './Banner.svelte';

	export let details;
	export let isFooterVisible;
	export let isPinned;

	$: footerText = isPinned
		? $_orgBannerFooter
		: isFooterVisible && 'Click to pin org';
</script>

<Banner
	{_screen}
	{footerText}
	isWide={true}
	on:close={clearHero}
>
	<DetailCard
		item={details}
		slot='body'
		showAsFocused={true}
		shouldFocusOrg={false}
	/>
</Banner>
