<script>
	import { setContext } from "svelte";
	import { writable } from "svelte/store";

	export let currentViewId;
	const _currentViewId = writable(0);
	let views = [];

	const viewsContext = setContext("views", {
		_currentViewId,
		addView,
		renderAnyway: false
	});

	function addView (id) {
		views = [...views, id];
	}

	$: $_currentViewId = currentViewId;
</script>

<div
	class='viewport'
>
	<slot />
</div>

<style>
	.viewport {
		width: 100%;
		height: 100%;
	}
</style>
