<script>
	import {setContext} from 'svelte';
	import {writable} from 'svelte/store';

	export let currentViewId;

	const _currentViewId = writable(0);

	const registerView = () => {}

	setContext('viewport', {
		_currentViewId,
		registerView,
		viewType: 'xor'
	});

	$: $_currentViewId = currentViewId;
</script>

<div
	class='viewport'
>
	<slot />
</div>

<style>
	.viewport {
		width: 100%;
		height: 100%;
	}
</style>
