<script>
	import {setContext} from 'svelte';
	import {writable} from 'svelte/store';

	export let viewId;

	const _viewId = writable(0);

	const registerView = () => {}

	setContext('viewport', {
		_viewId,
		registerView,
		viewType: 'xor'
	});

	$: $_viewId = viewId;
</script>

<div class='viewport'>
	<slot />
</div>

<style>
	.viewport {
		width: 100%;
		height: 100%;
	}
</style>
