<script>
	import {_screen} from '@svizzle/ui/src/sensors/screen/ScreenSensor.svelte';

	import DetailCard from 'app/components/details/DetailCard.svelte';
	import LayoutMF from 'app/components/svizzle/LayoutMF.svelte';
	import Banner from 'app/components/svizzle/Banner.svelte';

	import {
		_hero,
		_isCursorOnMap,
		clearHero,
		defaultFooterText
	} from 'app/stores/interaction';

	$: footerText = $_hero.isPinned
		? $_isCursorOnMap ? 'Click to unpin' : defaultFooterText
		: 'Click to pin';
</script>

<Banner
	{_screen}
	{footerText}
	isWide={true}
	on:close={clearHero}
>
	<LayoutMF {footerText}>
		<DetailCard
			item={$_hero.org}
			slot='main'
			showAsFocused={true}
			shouldFocusOrg={false}
		/>
	</LayoutMF>
</Banner>
