<script>
	import {_evaluatorSettings} from 'app/stores/eval';
	import Input from 'app/components/Input.svelte';
	import {emailRegex} from 'app/utils/eval';

	let userEmail = '';

	const onSubmitEmail = () => {
		if (isValidEmail) {
			$_evaluatorSettings = {userEmail};
		}
	};

	$: isValidEmail = emailRegex.test(userEmail);
</script>

<div class='emailForm'>
	<div class='controls'>
		<Input
			autofocus=true
			bind:value={userEmail}
			borderColor={!isValidEmail && 'red'}
			on:submitted={onSubmitEmail}
			placeholder='Please insert your email'
			type='email'
		/>
		<button
			on:click={onSubmitEmail}
			disabled={!isValidEmail}
		>
			Start
		</button>
	</div>
</div>

