<script>
	import {createEventDispatcher} from 'svelte';

	export let isVerticalLayout = false;

	const dispatch = createEventDispatcher();

	const onVoteClick = vote => {
		dispatch('voted', vote);
	};

</script>

<div
	class='controls'
	class:vertical={isVerticalLayout}
>
	<button
		on:click={() => onVoteClick('keep')}
		class='keep'
	>
		<div>
			&#128077;
		</div>
		Yes
	</button>
	<button
		on:click={() => onVoteClick('dismiss')}
		class='dismiss'
	>
		<div>
			&#10060;
		</div>
		No
	</button>
	<button
		on:click={() => onVoteClick('dunno')}
		class='dunno'
	>
		<div>
			&#129335;
		</div>
		Not sure
	</button>
</div>

<style>
	.controls {
		align-items: center;
		display: grid;
		grid-auto-flow: column;
		justify-items: center;
		padding: 0 1em;
