<script>
	import {capitalize} from '@svizzle/utils';
	import Icon from '@svizzle/ui/src/icons/Icon.svelte';
	import Info from '@svizzle/ui/src/icons/feather/Info.svelte';
	import Menu from '@svizzle/ui/src/icons/feather/Menu.svelte';
	import Send from '@svizzle/ui/src/icons/feather/Send.svelte';
	import X from '@svizzle/ui/src/icons/feather/X.svelte';
	import A11yPerson from '@svizzle/ui/src/icons/svizzle/A11yPerson.svelte';
	import Link from '@svizzle/ui/src/Link.svelte';
	import {_screen} from '@svizzle/ui/src/sensors/screen/ScreenSensor.svelte';

	import {_currThemeVars} from 'app/stores/theme';
	import theme from 'app/theme';
	import {version} from 'app/utils/version';

	const changelogUrl = 'https://github.com/nestauk/dap_uk_ai_map/blob/staging/CHANGELOG.md';

	export let contentHeight;
	export let segment;
	export let showA11yMenu = false;
	export let isA11yDirty = false;

	let showMenu = false;

	const closeMenu = () => {
		showMenu = false;
	}
	const toggleA11yMenu = event => {
		showA11yMenu = !showA11yMenu;
		event.target.setAttribute('aria-expanded', showA11yMenu.toString());
	}
	const toggleMenu = event => {
		showMenu = !showMenu;
		event.target.setAttribute('aria-expanded', showMenu.toString());
	}

	$: makeColor = id => segment === id ? theme.colorLink : undefined;
	$: a11yStrokeColor = isA11yDirty
		? $_currThemeVars['--color-background-main']
		: $_currThemeVars['--color-main)'];
	$: a11yFillColor = isA11yDirty ? $_currThemeVars['--color-main)'] : '';
</script>

<nav
	class='NavSmall {$_screen?.classes}'
	role='none'
	style='--content-height: {contentHeight}px'
>
		<header>
			{segment ? capitalize(segment) : 'Home'}
		</header>
		<button
			aria-label='Accessibility settings'
			class='clickable'
			on:click={toggleA11yMenu}
		>
			<Icon
				fill={a11yFillColor}
				glyph={A11yPerson}
				stroke={a11yStrokeColor}
				strokeWidth=1
			/>
		</button>
		<button
			aria-label='Website links'
			class='clickable'
			on:click={toggleMenu}
		>
			{#if showMenu}
				<Icon glyph={X} />
			{:else}
				<Icon glyph={Menu} />
			{/if}
		</button>
	{#if showMenu}
		<menu on:click={closeMenu}>
			<div role='none'>
				<ul role='none'>
					<li
						class:selected='{segment === undefined}'
						role='none'
					>
						<Link
							href='.'
							theme={{color: makeColor('.')}}
						>
							Home
						</Link>
					</li>
					<li
						class:selected='{segment === 'map'}'
						role='none'
					>
						<Link
							href='map'
							theme={{color: makeColor('map')}}
						>
							Map
						</Link>
					</li>
					<li
						class:selected='{segment === 'methodology'}'
						role='none'
					>
						<Link
							href='methodology'
							theme={{color: makeColor('methodology')}}
						>
							Methodology
						</Link>
					</li>
					<li
						class:selected='{segment === 'guides'}'
						role='none'
					>
						<Link
							href='guides'
							theme={{color: makeColor('guides')}}
						>
							Guides
						</Link>
					</li>
					<li
						class:selected='{segment === 'accessibility'}'
						role='none'
					>
						<Link
							href='accessibility'
							theme={{color: makeColor('accessibility')}}
						>
							Accessibility
						</Link>
					</li>
				</ul>
			</div>
			<div role='none'>
				<ul role='none'>
					<!--
					<li role='none'>
						<Link
							download
							href={zipUrl}
						>
							Download
							<Icon
								glyph={Download}
								size=20
								strokeWidth=1.5
							/>
						</Link>
					</li>
					-->
					<li
						class:selected='{segment === 'feedback'}'
						role='none'
					>
						<Link
							href='feedback'
							rel='prefetch'
							theme={{color: makeColor('feedback')}}
						>
							Feedback
							<Icon
								glyph={Send}
								size=20
								strokeWidth=1.5
							/>
						</Link>
					</li>
					<li
						class:selected='{segment === 'info'}'
						role='none'
					>
						<Link
							href='info'
							rel='prefetch'
							theme={{color: makeColor('info')}}
						>
							Info
							<Icon
								glyph={Info}
								size=20
								strokeWidth=1.5
							/>
						</Link>
					</li>
					<li
						aria-label='Source code repository'
						role='none'
					>
						<Link
							href={changelogUrl}
							type='external'
							theme={{
								color: $_currThemeVars['--color-main'],
								iconStroke: $_currThemeVars['--color-main']
							}}
						>
							{version}
						</Link>
					</li>
				</ul>
			</div>
		</menu>
	{/if}
</nav>

<style>
	nav, menu {
		align-items: center;
		display: flex;
		height: 100%;
		width: 100%;
		z-index: var(--z-1000);
	}
	nav {
		gap: 12px;
		justify-content: end; /* Align to the right on Firefox */
		justify-content: flex-end; /* Align to the right on Chrome */
		position: relative;
	}
	header {
		color: var(--color-link);
		font-weight: bold;
		margin: 0 auto;
		position: absolute;
		text-align: center;
		width: 100%;
	}
	div {
		z-index: var(--z-1000);
	}
	menu {
		background: var(--color-background-main);
		flex-direction: column-reverse;
		height: var(--content-height);
		justify-content: space-evenly;
		left: 0;
		position: fixed;
		top: 0;
		width: 100%;
	}
	ul {
		align-items: center;
		display: flex;
		flex-direction: column-reverse;
		margin: 0;
		padding: 0;
	}
	li {
		display: block;
		padding: 1em 0.5em;
		white-space: nowrap;
	}

	.xSmall li {
		padding: 0.25em;
	}

	.landscape menu {
		flex-direction: row;
	}

	button {
		border: none;
		background: transparent;
		color: var(--color-main);
		width: min-content;
		height: min-content;
		z-index: var(--z-1000);
	}
