<script>
	import geoViewport from '@mapbox/geo-viewport';
	import mapboxgl from 'mapbox-gl';
	import {beforeUpdate, getContext, onMount} from 'svelte';

	import {_bbox_WSEN} from 'app/stores/selection';
	import {clearHero} from 'app/stores/interaction';

	import {
		FIT_PADDING,
		MAPBOXGL_MAX_ZOOM,
		MAPBOXGL_MIN_ZOOM,
		MAPBOXGL_TILE_SIZE
	} from './consts';

	export let accessToken = null;
	export let CustomLayers = null;
	export let getLonLat;
	export let items = [];
	export let styleURL;
	export let withScaleControl = true;
	export let withZoomControl = true;

	/* state */

	let doesMapExist;
	let height = 0;
	let isInteractive = true; // has to be true initially
	let map;
	let mapcontainer;
	let projectFn;
	let width = 0;

	/* machine */
	const __bus = getContext('__bus');

	/* bbox */

	$: viewport = geoViewport.viewport(
		$_bbox_WSEN,
		[width, height],
		MAPBOXGL_MIN_ZOOM,
		MAPBOXGL_MAX_ZOOM,
		MAPBOXGL_TILE_SIZE
	);

	/* layers */

	let customLayers;

	$: projectFn && customLayers && customLayers.$set({projectFn});
	$: getLonLat && height && items && width &&
		customLayers && customLayers.$set({
		getLonLat,
		height,
		items,
		width,
	});

	/* controls */

	const addAttributionControl = () => {
		map.addControl(
			new mapboxgl.AttributionControl({
				compact: true
			})
		)
	};

	const addScaleControl = () => {
		map.addControl(
			new mapboxgl.ScaleControl({
				maxWidth: 80,
				unit: 'metric'
			}),
			'bottom-right'
		);
	};

	const addZoomControl = () => {
		map.addControl(
			new mapboxgl.NavigationControl({
				showCompass: false
			}),
			'bottom-left'
		);
	};

	const addControls = () => {
		addAttributionControl();

		if (withScaleControl) {
			addScaleControl();
		}

		if (withZoomControl) {
			addZoomControl();
		}
	};

	/* bbox */

	const fitToBbox = () => {
		map.fitBounds($_bbox_WSEN, {
			linear: true,
			padding: {
				bottom: FIT_PADDING,
				left: FIT_PADDING,
				right: FIT_PADDING,
				top: FIT_PADDING,
			}
		});
	};

	/* custom layers */

	const addCustomLayers = () => {
		if (CustomLayers) {
			const canvasContainer = map.getCanvasContainer();

			customLayers = new CustomLayers({
				target: canvasContainer,
				props: {
					getLonLat,
					height,
					items,
					width,
					projectFn: x => map.project(x)
				}
			});
		}
	};

	/* events */

	const updateBbox = () => {
		const bounds = map.getBounds().toArray();
		// _bbox_WS_EN.set(bounds);
		__bus.send(
			'UPDATED_BBOX',
			{bbox_WS_EN: bounds}
		)
	};

	const updateProjection = () => {
		if (map) {
			projectFn = x => map.project(x);
		}
	};

	const setMapEvents = () => {
		map.on('move', () => {
			updateProjection();
			updateBbox();
		});
	}

	/* methods */

	// FIXME TBD: bind instead?
	const setGeometry = () => {
		if (!mapcontainer) {
			return
		}

		const elementGeometry = getComputedStyle(mapcontainer);
		width = parseFloat(elementGeometry.width);
		height = parseFloat(elementGeometry.height);
	};

	const createMap = () => {
		const {center, zoom} = viewport;

		mapboxgl.accessToken = accessToken;

		map = new mapboxgl.Map({
			container: mapcontainer,
			center,
			// projection: 'equalEarth',
			renderWorldCopies: false,
			style: styleURL,
			zoom,

			// interactions
			attributionControl: false, // we add this later to have it compact
			dragPan: isInteractive,
			dragRotate: false,
			pitchWithRotate: false, // don't render dots in perspective
			scrollZoom: isInteractive,
			touchZoomRotate: false
		})
		.on('load', () => {
			addCustomLayers();
			setMapEvents();
			fitToBbox();

			setGeometry(); // ipad FIXME: initial svg is 100x100
		})
		.on('click', () => {
			clearHero();
		});

		// controls

		addControls();
	};

	/* lifecycle */

	beforeUpdate(() => {
		if (viewport && height > 0 && !doesMapExist) {
			doesMapExist = true;
			createMap();
		}
	})

	onMount(() => {
		setGeometry();
	});
</script>

<svelte:head>
	<link
		href='/css/mapbox-gl.css'
		rel='stylesheet'
		type='text/css'
	>
</svelte:head>

<svelte:window on:resize={setGeometry} />

<div class='MapboxglBase'>
	<div
		bind:this={mapcontainer}
		class='mapcontainer'
	></div>
</div>

<style>
	.MapboxglBase {
		height: 100%;
		position: relative;
		width: 100%;
	}

	.mapcontainer {
		height: 100%;
		width: 100%;
	}
</style>
