<script>
	import PieSector from './PieSector.svelte';
	export let cx;
	export let cy;
	export let r;
	export let colors = ['black'];

	$: colors = colors || ['black'];
	$: sectorCount = colors.length;
	$: angleSpan = 2 * Math.PI / sectorCount;
</script>

{#each colors as color, i}
	<PieSector
		{cx}
		{cy}
		{r}
		angleStart={i*angleSpan}
		angleSpan={angleSpan}
		fill={color}
	/>
{/each}
