<script>
	import Link from '@svizzle/ui/src/Link.svelte';

	import {_currThemeVars} from 'app/stores/theme';

	export let href;

	$: theme = {
		color: $_currThemeVars['--color-link'],
		iconStroke: $_currThemeVars['--color-link']
	};
	$: type = href && href.startsWith('http') && 'external' || null;
</script>

<Link
	{...$$props}
	{theme}
	{type}
	isBold={true}
	showIcon={true}
><slot/></Link>
