<script>
	import * as _ from 'lamb';

	import {isRegexpNotEmpty} from 'app/utils/svizzle/utils';

	const defaultColor = 'yellow';
	const defaultString = '';

	export let color = defaultColor;
	export let regex;
	export let string = defaultString;

	const splitText = (text, regExp) => {
		let pairs = [[text, '']];

		if (regExp && isRegexpNotEmpty(regExp)) {
			const remains = text.split(regExp);

			if (remains.length > 1) {
				const matches = text.match(regExp)?.concat('');
				pairs = _.zip(remains, matches);
			}
		}

		return pairs;
	}

	$: string = string || defaultString;
	$: parts = splitText(string, regex);
	$: color = color || defaultColor;
	$: style = `background-color:${color}`;
</script>

{#each parts as [normal, styled], i}{normal}{#if styled}<span {style} id='highlighted-{i}'>{styled}</span>{/if}{/each}
