<script>
	import {setupResizeObserver} from '@svizzle/ui/src/actions/resizeObserver';

	let main;
	let hasTopShadow;
	let hasBottomShadow;
	let tsOpacity;
	let bsOpacity;

	const {
		_writable: _mainSize,
		resizeObserver: mainSizeObserver
	} = setupResizeObserver();

	const onScroll = () => {
		hasTopShadow = main.scrollTop > 0;
		hasBottomShadow = main.scrollHeight > main.scrollTop + main.offsetHeight;
		tsOpacity = main.scrollTop < 10 ? main.scrollTop / 10 : 1;
		bsOpacity = main.scrollHeight - main.scrollTop - main.offsetHeight < 10
			? (main.scrollHeight - main.scrollTop - main.offsetHeight) / 10 : 1;
	};

	$: headerPaddingTop = $$slots.header ? '1em' : 0;
	$: style = `
	--headerPaddingTop: ${headerPaddingTop};
	--ts-opacity: ${tsOpacity};
	--bs-opacity: ${bsOpacity};
	`;
	$: (main && $_mainSize) && onScroll();
</script>

<div class='LayoutHMF' {style}>
	<header>
		<slot name='header' />
	</header>
	<main
		bind:this={main}
		class:shadowTop={hasTopShadow}
		class:shadowBottom={hasBottomShadow}
		on:scroll={onScroll}
		use:mainSizeObserver
	>
		<slot name='main' />
	</main>
	<footer>
		<slot name='footer' />
	</footer>
</div>

<style>
	.LayoutHMF {
		display: grid;
		grid-template-areas: 'header' 'main' 'footer';
		grid-template-rows: min-content 1fr min-content;
		height: 100%;
		overflow: auto;
		width: 100%;
	}

	header {
		grid-area: header;
		padding-left: 1em;
		padding-top: var(--headerPaddingTop);
	}
	main {
		grid-area: main;
		overflow: auto;
		position: relative;
		z-index: 1;
	}
	footer {
		font-style: italic;
		grid-area: footer;
		padding: 0.5em 1em;
		text-align: right;
	}

	.shadowTop {
		box-shadow: inset 0px 12px 13px -13px rgba(0,0,0, var(--ts-opacity));
	}
	.shadowBottom {
		box-shadow: inset 0px -12px 13px -13px rgba(0,0,0, var(--bs-opacity));
	}
	.shadowTop.shadowBottom {
		box-shadow:
			inset 0px 12px 13px -13px rgba(0,0,0, var(--ts-opacity)),
			inset 0px -12px 13px -13px rgba(0,0,0, var(--bs-opacity));
	}
</style>
