<script>
	import {setupResizeObserver} from '@svizzle/ui/src/actions/resizeObserver';

	let main;
	let hasTopShadow;
	let hasBottomShadow;

	const {
		_writable: _mainSize,
		resizeObserver: mainSizeObserver
	} = setupResizeObserver();

	const onScroll = () => {
		hasTopShadow = main.scrollTop > 0;
		hasBottomShadow = main.scrollHeight > main.scrollTop + main.offsetHeight;
	}

	$: headerPaddingTop = $$slots.header ? '1em' : 0;
	$: style = `--headerPaddingTop: ${headerPaddingTop}`;
	$: (main && $_mainSize) && onScroll();
</script>

<div class='LayoutHMF' {style}>
	<header>
		<slot name='header' />
	</header>
	<main
		bind:this={main}
		class:shadowTop={hasTopShadow}
		class:shadowBottom={hasBottomShadow}
		on:scroll={onScroll}
		use:mainSizeObserver
	>
		<slot name='main' />
	</main>
	<footer>
		<slot name='footer' />
	</footer>
</div>

<style>
	.LayoutHMF {
		display: grid;
		grid-template-areas: 'header' 'main' 'footer';
		grid-template-rows: min-content 1fr min-content;
		height: 100%;
		overflow: auto;
		width: 100%;
	}

	header {
		grid-area: header;
		padding-left: 1em;
		padding-top: var(--headerPaddingTop);
	}
	main {
		grid-area: main;
		overflow: auto;
	}
	footer {
		font-style: italic;
		grid-area: footer;
		padding: 0.5em 1em;
		text-align: right;
	}

	.shadowTop {
		background-image:
			linear-gradient(to bottom, lightgray, rgba(211, 211, 211, 0));
		background-position: top center;
		background-repeat: no-repeat;
		background-size: 100% 8px;
		background-attachment: fixed;
	}
	.shadowBottom {
		background-image:
		linear-gradient(to top, lightgray, rgba(211, 211, 211, 0));
		background-position: bottom center;
		background-repeat: no-repeat;
		background-size: 100% 8px;
		background-attachment: fixed;
	}
	.shadowTop.shadowBottom {
		background-image:
			linear-gradient(to bottom, lightgray, rgba(211, 211, 211, 0)),
			linear-gradient(to top, lightgray, rgba(211, 211, 211, 0));
		background-position: top center, bottom center;
		background-repeat: no-repeat, no-repeat;
		background-size: 100% 8px, 100% 8px;
		background-attachment: fixed, fixed;
	}
</style>
