<script>
	import Banner from './Banner.svelte';
	import LayoutHMF from 'app/components/svizzle/LayoutHMF.svelte';
	import Scroller from 'app/components/svizzle/Scroller.svelte';

	export let _screen;
	export let components;
	export let currentIndex;
	export let footerText = 'Click on background to dismiss';

	let isActive = false;

	function init () {
		currentIndex = 0;
		isActive = true;
	}

	function next () {
		currentIndex++;
		if (currentIndex >= components.length) {
			isActive = false;
		}
	}

	$: components?.length > 0 && init();
</script>

{#if isActive}
	<Banner
		{_screen}
		on:close={next}
	>
		<LayoutHMF>
			<div slot='main'>
				<Scroller>
					<svelte:component
						this={components?.[currentIndex]}
					/>
				</Scroller>
			</div>
			<p slot='footer'>{footerText}</p>
		</LayoutHMF>
	</Banner>
{/if}

<style>
	div {
		padding: 1.0em;
		height: 100%;
		position: relative;
	}
</style>
