import {send} from 'xstate/lib/actions';
import {
	_bbox_WS_EN,
	_placesSearchValue,
	_orgSearchValue,
	_orgTypesSelectionMode,
	toggleOrgType
} from 'app/stores/selection';

export default {
	toggleOrgType: (_, {id}) => {
		toggleOrgType(id);
	},
	toggleOrgTypeMode: (_, {orgTypesSelectionMode}) => {
		_orgTypesSelectionMode.set(orgTypesSelectionMode);
	},
	setOrgSearchValue: (_, {orgSearchValue}) => {
		_orgSearchValue.set(orgSearchValue)
	},
	setPlacesSearchValue: (_, {placesSearchValue}) => {
		_placesSearchValue.set(placesSearchValue);
	},
	setBBox_WS_EN: (_, {bbox_WS_EN}) => {
		_bbox_WS_EN.set(bbox_WS_EN);
	},
	sendCommitted: send('COMMITTED'),
	sendEdited: send('EDITED')
};
