import { createMachina } from '../utils';

import { editingOptions } from 'app/machines/map/editing.options';
import { historyOptions } from './history.options';
import { routeConfig } from './route.config';
import { createEditingStores } from 'app/machines/map/editing.context';

export const options = {
	actions: {
		...editingOptions.actions,
		...historyOptions.actions,
	}
};

export const createMapMachine = () => createMachina(
	routeConfig,
	options,
	createEditingStores()
);
