import editingStateNodes from './editing.stateNodes';
import historyStateNodes from './history.stateNodes';

export default {
	id: 'MapRoute',
	initial: 'PageLanding',
	states: {
		PageLanding: {
			invoke: {
				id: 'FetchingData',
				src: 'loadData',
				onDone: {
					target: '#MapRoute.PageInteractive',
					actions: [
						'updateDataset',
						'updateEditingStores'
					]
				},
				onError: {
					"target": "#MapRoute.PageError"
				}
			}
		},
		PageError: {},
		PageInteractive: {
			on: {
				ROUTE_CHANGED: {
					target: '#MapRoute.PageInteractive',
					actions: [
						'updateEditingStores'
					]
				}
			},
			states: {
				Editing: editingStateNodes,
				History: historyStateNodes,
			},
			type: 'parallel'
		}
	}
};
