import {get, writable} from 'svelte/store';
import {getNextOrg} from 'app/utils/eval';

export const _currentOrg = writable();
export const _evaluatorSettings = writable();
export const defaultEvaluatorSettings = null;


export const loadNextOrg = async userId => {
	const {userEmail} = get(_evaluatorSettings);
	const result = await getNextOrg(userEmail);
	_currentOrg.set(result.org);
};
