/* String -> String */

export const toLowerCase = str => str.toLowerCase();

export const sanitiseRegexInput =
	str => str.replace(/[#-.]|[[-^]|[?|{}]/ugi, '\\$&');

/* Regexp -> Boolean */

export const isRegexpEmpty = regexp => regexp.source === '(?:)';
export const isRegexpNotEmpty = regexp => regexp.source !== '(?:)';
